``` rust
pub fn str_join(strs: &[AsRef<str>], join: str) -> String {
    strs.iter().fold(String::new(), |mut acc, s| {
        if !acc.is_empty() { acc.push_str(join); }
        acc.push_str(s);
        acc
    });
}

fn main() {}
