// Test incremental compilation tracking where we change nothing
// in between revisions (hashing should be stable).

// revisions:rpass1 rpass2
// compile-flags: -Z query-dep-graph

#![feature(rustc_attrs)]
#![allow(dead_code)]
#![allow(non_snake_case)]
#![allow(unused_variables)]
#![rustc_partition_reused(module="struct_change_nothing", cfg="rpass2")]

#[cfg(rpass1)]
pub struct X {
    pub x: u32,
}

#[cfg(rpass2)]
pub struct X {
    pub x: u32,
}

#[cfg(rpass1)]
impl X {
    fn get(&self) -> u32 {
        self.x
    }
    pub fn dbl(&self) -> u32 {
        self.get() + self.get()
    }
}

#[cfg(rpass2)]
impl X {
    fn get(&self) -> u32 {
        self.x
    }
    pub fn dbl(&self) -> u32 {
        self.get() + self.get()
    }
}

pub struct EmbedX {
    x: X,
}

pub struct Y {
    pub y: char,
}

#[rustc_clean(label="TypeckTables", cfg="rpass2")]
pub fn use_X() -> u32 {
    let x: X = X { x: 42 };
    x.x as u32
}

#[rustc_clean(label="TypeckTables", cfg="rpass2")]
pub fn use_EmbedX(x: EmbedX) -> u32 {
    let x: X = X { x: 42 };
    x.x as u32
}

#[rustc_clean(label="TypeckTables", cfg="rpass2")]
pub fn use_Y() {
    let x: Y = Y { y: 'c' };
}

pub fn main() {}
