    value: u32
}

impl NonZeroU32 {
    const unsafe fn new_unchecked(value: u32) -> Self {
        NonZeroU32 { value }
    }
}

//pub const FOO_ATOM: NonZeroU32 = unsafe { NonZeroU32::new_unchecked(7) };
