use std::ops::Index;
use std::cmp::Ordering;

#[inline]
pub fn bsearch<T: PartialOrd + PartialEq>(data: &[T], item: &T) -> Option<usize> {
    let (mut l, mut u) = (0, data.len());
    loop {
        let mp = (l + u) / 2;
        match data[mp].partial_cmp(item)? {
            Ordering::Equal => return Some(mp),
            Ordering::Less => l = mp,
            Ordering::Greater => u = mp,
        }
        if l == u {
            return None;
        }
    }
}
