#![feature(const_generics)]

#[derive(PartialEq,Eq)]
enum Foo { bar }

fn baz<const T: Foo>() {}

const foobar : Foo = Foo::bar;

fn main() {
    //error[E0573]: expected type, found variant `Foo::bar`
    baz::<Foo::bar>();
    
    //however, this compiles
    baz::<foobar>();
}
```

