```rust
pub mod foo {
    pub trait Foo: sealed::Bar {
        fn foo(&self) {}
    }
    mod sealed {
        pub trait Bar {
            fn bar(&self) {}
        }
    }
    impl Foo for u32 {}
    impl sealed::Bar for u32 {}
}


fn meow_dyn(f: &dyn foo::Foo) {
    f.foo();
    f.bar();
}

fn meow_static(f: impl foo::Foo) {
    f.foo();
    f.bar();
}

fn meow_u32(f: u32) {
    use foo::Foo;
    // use crate::foo::sealed::Bar;
    // ^ doesn't work because it is private
    f.foo();
    f.bar(); // Why does this error now?
}


fn main() {
    meow_static(42);
    meow_dyn(&42);
}
```
