{
  pub fn getnode(&mut self, key:&str) -> Option<&mut Trienode<T>>
  {
      let mut current = self;
      for c in key.chars()
      {
         match current.children.get_mut(&c) {
	   None => { return None; },
	   Some(child) => { current=child; },
	 }      
      }
      Some(current)
  }//getnode

