#[derive(Debug)]                                                                                                                                                                                                                                                                                                                                                            
enum Bool { True, False }                                                                                                                                                                                                                                                                                                                                                   
                                                                                                                                                                                                                                                                                                                                                                            
fn main() {                                                                                                                                                                                                                                                                                                                                                                 
    println!("Hello, world! {:.3?}", false);                                                                                                                                                                                                                                                                                                                                
    println!("Hello, world! {:.3?}", Bool::False);                                                                                                                                                                                                                                                                                                                          
    println!("Hello, world! {:.3?}", &"false");                                                                                                                                                                                                                                                                                                                             
}   
